import{_ as a,c as e,o as t,a2 as l}from"./chunks/framework.C94oF1kp.js";const p=JSON.parse('{"title":"Сбалансированные деревья поиска. Красно-черные деревья.","description":"","frontmatter":{},"headers":[],"relativePath":"tasks/027.md","filePath":"tasks/027.md"}'),i={name:"tasks/027.md"},o=l('<h1 id="сбалансированные-деревья-поиска-красно-черные-деревья" tabindex="-1">Сбалансированные деревья поиска. Красно-черные деревья. <a class="header-anchor" href="#сбалансированные-деревья-поиска-красно-черные-деревья" aria-label="Permalink to &quot;Сбалансированные деревья поиска. Красно-черные деревья.&quot;">​</a></h1><h2 id="определение" tabindex="-1">Определение <a class="header-anchor" href="#определение" aria-label="Permalink to &quot;Определение&quot;">​</a></h2><p><strong>Красно-черное дерево (Red-Black Tree)</strong> — это разновидность сбалансированного бинарного дерева поиска, которая обеспечивает логарифмическое время выполнения основных операций (вставка, удаление, поиск). Такое дерево используется для поддержания отсортированных данных и часто используется в реализации ассоциативных массивов и других абстрактных типов данных.</p><h2 id="cвоиства" tabindex="-1">Cвойства <a class="header-anchor" href="#cвоиства" aria-label="Permalink to &quot;Cвойства&quot;">​</a></h2><ul><li>Каждый узел окрашен в красный или черный цвет.</li><li>Корень дерева всегда черный.</li><li>Все листья (NULL-узлы) считаются черными.</li><li>Если узел красный, то оба его потомка черные (нет двух красных узлов подряд).</li><li>Для каждого узла все пути от него до всех его потомков-листьев содержат одинаковое количество черных узлов.</li></ul><h2 id="операции" tabindex="-1">Операции <a class="header-anchor" href="#операции" aria-label="Permalink to &quot;Операции&quot;">​</a></h2><ol><li><p>При <strong>вставке</strong> нового узла красно-черное дерево может нарушить свои свойства. Для восстановления баланса дерева применяются следующие операции</p><ul><li><strong>Перекрашивание:</strong> Изменение цвета узлов.</li><li><strong>Левый и правый повороты:</strong> Повороты узлов для поддержания баланса дерева.</li></ul></li><li><p>При <strong>удалении</strong> узла также могут быть нарушены свойства красно-черного дерева. Процесс удаления включает в себя:</p><ul><li>Замещение удаляемого узла подходящим кандидатом (обычно минимальным элементом из правого поддерева или максимальным из левого).</li><li>Применение аналогичных техник, как и при вставке, для поддержания баланса дерева. <img src="https://camo.githubusercontent.com/3fe9f7e0b5ae2d4472c34dc45d4c677111d2ecc181614e6cc715d761366e5ce8/68747470733a2f2f75706c6f61642e77696b696d656469612e6f72672f77696b6970656469612f636f6d6d6f6e732f362f36362f5265642d626c61636b5f747265655f6578616d706c652e737667" alt=""></li></ul></li></ol>',7),r=[o];function s(c,n,d,h,_,f){return t(),e("div",null,r)}const m=a(i,[["render",s]]);export{p as __pageData,m as default};
