import{_ as e,c as a,o as t,a2 as o}from"./chunks/framework.C94oF1kp.js";const u=JSON.parse('{"title":"Динамическое программирование. Задача о счастливых билетах.","description":"","frontmatter":{},"headers":[],"relativePath":"tasks/042.md","filePath":"tasks/042.md"}'),n={name:"tasks/042.md"},i=o('<h1 id="динамическое-программирование-задача-о-счастливых-билетах" tabindex="-1">Динамическое программирование. Задача о счастливых билетах. <a class="header-anchor" href="#динамическое-программирование-задача-о-счастливых-билетах" aria-label="Permalink to &quot;Динамическое программирование. Задача о счастливых билетах.&quot;">​</a></h1><h2 id="условие" tabindex="-1">Условие <a class="header-anchor" href="#условие" aria-label="Permalink to &quot;Условие&quot;">​</a></h2><p><strong>Задача о счастливых билетах:</strong> билет состоящий из 2n цифр называется счастливым если сумма первых n цифр равна сумме последних n цифр.</p><p>Необходимо посчитать кол-во счастливых билетов например для n=3 (длина билета 6).</p><h2 id="решение" tabindex="-1">Решение <a class="header-anchor" href="#решение" aria-label="Permalink to &quot;Решение&quot;">​</a></h2><p>Для решения данной задачи необходимо построить специальную таблицу от n = 1, до необходимого нам n.</p><p><img src="https://lh7-us.googleusercontent.com/docsz/AD_4nXe4_H7ekhiqaYyHZzygmRe5jqifIuxhHGU5V833YoZn859Ld39sHlIajtmDeaCpoqSx55Z1pHn42Yi1vWOytjk_1NyMrhjcyYFlCdTqhOrYcQ8ku8k36GvwVXdNqHEEnAq3LXy6ZQ9wwJ1J0GIhHgzOE-hM?key=9gziK4gT-jwK64_BpOeehQ" alt=""></p><p>Во всех колонка рассматриваются  ячейки от 0 до 9 * n + 1 (n - текущее), для n=1  это 9 * 1 + 1, для n=2 это 9 * 2 + 1=19, и т.д.</p><ul><li>Первая колонка - базовый случай, заполняется “1”.</li><li>Пустые ячейки это “0”.</li><li>Если <code>k (индекс) меньше 10</code>, то значение ячейки равно сумме первых k ячеек левого столбца.</li><li>Если<code> k &gt;= 10</code>, то значении ячейки равно сумме 10 ячеек левого столбца начиная <code>с k до k-10+1</code></li></ul><p><strong>Ответом является сумма квадратов значений всех ячеек нужного нам столбца</strong></p><p>(Чтобы быстро создать таблицу)</p><ol><li>базовый случай, первая строка все “1”, первый столбец 1 * 9 + 1= 10 единиц</li><li>первые 10 строк (от 0 до 9 цифры)  для любого n, это сумма левой + верхней ячейки</li><li>начиная с k=10, значение ячейки равно сумме значений ячейки слева и ещё 9 ячеек вверх.  (сумма от k-10+1 до k)</li></ol>',12),l=[i];function s(r,c,d,_,h,p){return t(),a("div",null,l)}const m=e(n,[["render",s]]);export{u as __pageData,m as default};
